template <template <typename> class TCont, typename TType>
TCont<TType> fill_from(int count, std::istream& from)
{
    TCont<TType> seq;
    seq.reserve(count);

    std::copy(
        std::istream_iterator<TType>(from),
        std::istream_iterator<TType>(),
        std::inserter(seq, seq.end()));

    return seq;
}

