float dlugosc(Point a, Point b)
{
    float dlugosc;
    dlugosc=sqrt(((a.x-b.x)*(a.x-b.x))+((a.y-b.y)*(a.y-b.y)));
    return dlugosc;
}

float poletrojkata(Point a, Point b, Point c)
{
    float A = dlugosc(b, c);
    float B = dlugosc(a, c);
    float C = dlugosc(a, b);

    float p;
    p=(A + B + C)/2;
    return sqrt(p*(p-A)*(p-B)*(p-C));
}

