#include<bits/stdc++.h>
using namespace std;

typedef long long ll;
typedef pair<int, int> pii;

const int MAX = 2e6 + 15;
const int INF = 0x3f3f3f3f;
const int MOD = 1e9 + 7;

#define pb push_back
#define sz(x) (int) x.size()
#define fr first
#define sc second
#define mp make_pair
#define all(x) x.begin(), x.end()
#define dbg(x) cerr << #x << ": " << "[ " << x << " ]\n"

int n, m, t, ans, numBridge, bridge[MAX], low[MAX], pre[MAX], seen[MAX], tseen[MAX], used[MAX], cmp[MAX];
vector<pii> adj[MAX], edges;
vector<int> tree[MAX];
int mx, id;

void reset(){
    for(int i = 0; i <= n + 10; i++){
        low[i] = 0;
        pre[i] = 0;
        seen[i] = 0;
        tseen[i] = 0;
        used[i] = 0;
        cmp[i] = 0;

        adj[i].clear();
        tree[i].clear();
    }

    for(int i = 0; i <= m + 10; i++)
        bridge[i] = 0;

    t = 0, ans = 0, numBridge = 0;
    edges.clear();
}

void ldfs(int u, int p){ //low link to find bridges
    pre[u] = low[u] = t++;
    seen[u] = true;

    for(auto e : adj[u]){
        int v = e.fr, id = e.sc;
        if(v == p) continue;

        if(seen[v]){
            low[u] = min(low[u], pre[v]);

        }else{
            ldfs(v, u);
            low[u] = min(low[u], low[v]);

            if(low[v] > pre[u]){
                numBridge++;
                bridge[id] = true;
            }
        }
    }
}

void dfs1(int u, int p, int c){ //build components
    used[u] = true;
    cmp[u] = c;

    for(auto e : adj[u]){
        int v = e.fr, id = e.sc;
        
        if(bridge[id] || used[v]) continue;
        dfs1(v, u, c);
    }
}


int main(){
    ios_base::sync_with_stdio(false);
    cin.tie(NULL); 

    int tc; cin >> tc;

    while(tc--){
        cin >> n >> m;

        for(int i = 0; i < m; i++){
            int u, v; cin >> u >> v;
            adj[u].pb({v, i});
            adj[v].pb({u, i});

            edges.pb({u, v});
        }

        ldfs(1, -1);

        int comp = 1;

        for(int i = 1; i <= n; i++)
            if(!used[i]) dfs1(i, -1, comp++);

        for(int i = 0; i < m; i++){
            if(bridge[i]){
                int u = edges[i].fr, v = edges[i].sc;
                tree[cmp[u]].pb(cmp[v]);
                tree[cmp[v]].pb(cmp[u]);
            }
        }

        reset();
    }

    return 0;
}

