using namespace std;

// DSU
vector<int> parent;
vector<vector<int>> component;
int root(int i) {
    return parent[i] < 0 ? i : (parent[i] = root(parent[i]));
}
void unite(int i, int j) {
    i = root(i), j = root(j);
    if (i == j) return;
    if (parent[i] > parent[j]) swap(i, j);
    parent[i] += parent[j];
    component[i].insert(component[i].end(), component[j].begin(), component[j].end());
    parent[j] = i;
    component[j].clear();
}

int main() {
