    for (int i = 1; i <= n; ++i) cin >> holding[i][0] >> holding[i][1];
    vector<pair<int, int>> changes(m);
    for (int i = 0; i < m; ++i) {
        int monkey, hand; cin >> monkey >> hand;
        --hand;
        int &fellow = holding[monkey][hand];
        changes[i] = {monkey, fellow};
        fellow = -1;
