            hesh.second = (hesh.second * p + (s[j] - 'a' + ONE)) % m2;
        }
        keys[len_s][hesh.first * m1 + hesh.second] = i;
    }
    sort(all(rzl));
    rzl.resize(unique(all(rzl)) - rzl.begin());
    m = rzl.size();
    for (i = 0; i < len_t; i++) {
        for (j = 0; j < m; j++) {
            len = rzl[j];
            if (i + len - 1 < len_t) {
                hesh = get_h(i, i + len - 1);
                if (keys[len].contains(hesh.first * m1 + hesh.second)) {
                    base[i].push_back(len);
                    base2[i + len - 1].push_back(len);
                }
            }
        }
    }
    for (i = 0; i < len_t; i++) {
        sort(all(base2[i]));
    }
    cin >> q;
    for (i = 0; i < q; i++) {
        cin >> Q[i].l >> Q[i].r;
        Q[i].idx = i;
        Q[i].l--;
        Q[i].r--;
    }
    sort(Q, Q + q, cmp);
    int left, right, last_r, last_l;
    for (i = 0; i < q; i++) {
        if (Q[i].l / k != lst) {
            lst = Q[i].l / k;
            l = Q[i].l;
            r = Q[i].l - 1;
            res = 0;
        }
        last_r = r;
        last_l = l;
        for (r; r < Q[i].r; r++) {
            left = -1, right = base2[r + 1].size();
            while (right - left > 1) {
                m = (left + right) / 2;
                if (r + 1 - base2[r + 1][m] + 1 >= last_l) {
                    left = m;
                }
                else {
                    right = m;
                }
            }
            res += right;
        }
        for (l; l > Q[i].l; l--) {
            left = -1, right = base[l - 1].size();
            while (right - left > 1) {
                m = (left + right) / 2;
                if (l - 1 + base[l - 1][m] - 1 <= r) {
                    left = m;
                }
                else {
                    right = m;
                }
            }
            res += right;
        }
        for (l; l < Q[i].l; l++) {
            left = -1, right = base[l].size();
            while (right - left > 1) {
                m = (left + right) / 2;
                if (l + base[l][m] - 1 <= r) {
                    left = m;
                }
                else {
                    right = m;
                }
            }
            res -= right;
        }
        ans[Q[i].idx] = res;
    }
