public class Persona {
     // Atributos
    private String nombre;
    private String apellido;
    private String ciudad;
    private String nacionalidad;
    private int edad;
 
    // Constructor sin parámetros
    public Persona() {
        this.nombre = "";
        this.apellido = "";
        this.ciudad = "";
        this.nacionalidad = "";
        this.edad = 0;
    }
 
    // Constructor con 1 parámetro (nombre)
    public Persona(String nombre) {
        this.nombre = nombre;
        this.apellido = "";
        this.ciudad = "";
        this.nacionalidad = "";
        this.edad = 0;
    }
 
    // Constructor con 5 parámetros (nombre, apellido, ciudad, nacionalidad, edad)
    public Persona(String nombre, String apellido, String ciudad, String nacionalidad, int edad) {
        this.nombre = nombre;
        this.apellido = apellido;
        this.ciudad = ciudad;
        this.nacionalidad = nacionalidad;
        this.edad = edad;
    }
 
    // Getters y Setters
    public String getNombre() {
        return this.nombre;
    }
    
    public void setNombre(String nombre){
        this.nombre = nombre;
    }
    
    public String getApellido() {
        return this.apellido;
    }
    
    public void setApellido(String apellido){
        this.apellido = apellido;
    }
    
    public String getCiudad() {
        return this.ciudad;
    }
    
    public void setCiudad(String ciudad){
        this.ciudad = ciudad;
    }
    
    public String getNacionalidad() {
        return this.nacionalidad;
    }
    
    public void setNacionalidad(String nacionalidad){
        this.nacionalidad = nacionalidad;
    }
    
    public int getEdad() {
        return this.edad;
    }
    
    public void setEdad(int edad){
        this.edad = edad;
    }
    
    // Métodos
    
    // Mostrar todos los campos
    public String mostrarInfo(){
        return nombre + ", " + apellido + ", " + ciudad + ", " +                
                nacionalidad + ", " + edad;
    }
}

