void swap(int* p1, int* p2) {
  int t = *p1;
  *p1 = *p2;
  *p2 = t;
}


vector<int> Partition(vector<int> aa, int xx) {
  //делим на < и >=
  int nn = aa.size();
  int ll = -1;
  for (int ii = 0; ii < nn; ++ii) {
    if (aa[ii] < xx) {
      int* p1 = &aa[ii];
      int* p2 = &aa[ll + 1];
      swap(p1, p2);
      ll++;
    }
  }
  //делим на = и >
  for (int ii = ll + 1; ii < nn; ++ii) {
    if (aa[ii] == xx) {
      int* p1 = &aa[ii];
      int* p2 = &aa[ll + 1];
      swap(p1, p2);
      ll++;
    }
  }
  return aa;
}

int main() {
  std::ios::sync_with_stdio(false);
  std::cin.tie(0);
  std::cout.tie(0);
  int nn;
  cin >> nn;
  vector <int> aa(nn);
  for (int& ii: aa) {
    cin >> ii;
  }
  int xx;
  cin >> xx;
  aa = Partition(aa, xx);
  for (int ii: aa) {
    cout << ii << ' ';
  }
  cout << "\n";
}
