char *fileContents(const char path[]) {
    struct stat st;
    stat(path, &st);

    FILE *file = fopen(path, "r");
    if (file == NULL) {
        return NULL;
    }

    char *s = (char*)calloc(st.st_size + 1, sizeof(char));
    if (s == NULL) {
        fclose(file);
        return NULL;
    }
    fread(s, sizeof(char), st.st_size, file);
    fclose(file);
    return s;
}

