// 1.3.1. Создание текстового файла.
void createFile(const string& filename, const vector<vector<int>>& data) {
    ofstream outfile(filename);

    if (!outfile.is_open()) {
        cerr << "Не удалось создать файл." << endl;
        return;
    }

    for (const auto& row : data) {//Перебор сроки в матрице
        for (const int num : row) {//Перебор внутри строки
            outfile << num << " ";
        }
        outfile << "\n";
    }
    outfile.close();
}

// 1.3.2. Вывод содержимого текстового файла.
void displayFileContent(const string& filename) {
    ifstream infile(filename);

    if (!infile.is_open()) {
        cerr << "Не удалось открыть файл." << endl;
        return;
    }

    string line;
    while (getline(infile, line)) {
        cout << line << "\n";
    }
    infile.close();
}

// 1.3.3. Добавление новой строки в конец файла.
void appendToFile(const string& filename, const vector<int>& data) {
    ofstream outfile(filename, ios::app);

    if (!outfile.is_open()) {
        cerr << "Не удалось открыть файл." << endl;
        return;
    }
    //Приписываем одну строчку к файлу(из чисел)
    for (const int num : data) {
        outfile << num << " ";
    }
    //Закидываем переход на новую строку в файл
    outfile << "\n";
    outfile.close();
}

// 1.3.4. Прочитать значение числа, указав его порядковый номер в файле.
//мы спрашиваем функцию - что лежит под номером index
int readNumberByIndex(const string& filename, size_t index) {
    ifstream infile(filename);

    if (!infile.is_open()) {
        cerr << "Не удалось открыть файл." << endl;
        return -1;
    }

    size_t count = 0;
    string line;
    //Не знаем сколько строк в файле - читаем пока читается
    while (getline(infile, line)) {
        //создаем поток ввода из строки
        istringstream ss(line);
        int num;
        //Не знаем сколько чисел в строке - читаем, пока читаются, подсчитываем их
        while (ss >> num) {
            if (count == index) {
                infile.close();
                return num;
            }
            count++;
        }
    }
    infile.close();
    cerr << "Индекс вне диапазона." << endl;
    return -1;
}

// 1.3.5. Определить количество чисел в файле.
size_t countNumbersInFile(const string& filename) {
    ifstream infile(filename);

    if (!infile.is_open()) {
        cerr << "Не удалось открыть файл." << endl;
        return 0;
    }

    size_t count = 0;
    string line;
    while (getline(infile, line)) {
        istringstream ss(line);
        int num;
        while (ss >> num) {
            count++;
        }
    }
    infile.close();
    return count;
}

// 10. Создать новый файл из значений исходного, умножив каждое четное число на максимальное число в файле.
void createModifiedFile(const string& inputFilename, const string& outputFilename) {
    ifstream infile(inputFilename);

    if (!infile.is_open()) {
        cerr << "Не удалось открыть файл." << endl;
        return;
    }
    //В потенциальный максимум закидываем максимально маленькое число
    int maxNumber = INT_MIN;
    string line;
    //Перебирая все числа - находим максимум
    while (getline(infile, line)) {
        istringstream ss(line);
        int num;
        while (ss >> num) {
            if (num > maxNumber) {
                maxNumber = num;
            }
        }
    }
    infile.close();

    infile.open(inputFilename);
    ofstream outfile(outputFilename);

    while (getline(infile, line)) {
        istringstream ss(line);
        int num;
        while (ss >> num) {
            if (num % 2 == 0) {
                outfile << num * maxNumber << " ";
            }
            else {
                outfile << num << " ";
            }
        }
        outfile << "\n";
    }

    infile.close();
    outfile.close();
}

