template<class T, class U> class pair {
    public:
    T first;
    U second;
    bool operator < (const pair<T, U> b) const {
        if(this->first == b.first) {
            return this->second < b.second;
        }
        return this->first < b.first;
    }
};

void swap(pair<int, int> &a, pair<int, int> &b) {
    pair<int, int> tmp = a;
    a = b;
    b = tmp;
}

template<class T> class vector {
    T* arr;
    int sz;
    int cap;
public:
    vector() {
        sz = 0;
        cap = 1;
        arr = new T[cap];
    }
    void resize(int x) {
        T* tmp = new T[x];
        for(int i = 0; i < sz; i++) {
            tmp[i] = arr[i];
        }
        delete[] arr;
        arr = tmp;
        cap = sz = x;
    }
    void push_back(T x) {
        if (sz == cap) {
            T* tmp = new T[2 * cap];
            for (int i = 0; i < sz; i++) {
                tmp[i] = arr[i];
            }
            delete[] arr;
            arr = tmp;
            cap *= 2;
        }
        arr[sz++] = x;
    }
    void pop_back() {
        sz--;
    }
    int size() {
        return sz;
    }
    T& operator[](int i) {
        return arr[i];
    }
};

template<class T> class priority_queue {
    vector<T> heap;
    int size;
    bool cmp(T a, T b) {
        return b < a;
    }
    void heapify(int i) {
        int l = 2 * i + 1;
        int r = 2 * i + 2;
        int largest = i;
        if(l < size && cmp(heap[l], heap[i])) {
            largest = l;
        }
        if(r < size && cmp(heap[r], heap[largest])) {
            largest = r;
        }
        if(largest != i) {
            swap(heap[i], heap[largest]);
            heapify(largest);
        }
    }
public:
    priority_queue() {
        size = 0;
    }

    void push(T x) {
        heap.push_back(x);
        int i = size;
        size++;
        while (i > 0 && cmp(heap[i], heap[(i - 1) / 2])) {
            swap(heap[i], heap[(i - 1) / 2]);
            i = (i - 1) / 2;
        }
    }
    T top() {
        return heap[0];
    }
    void pop() {
        swap(heap[0], heap[size - 1]);
        size--;
        heap.pop_back();
        heapify(0);
    }
    int get_size() {
        return size;
    }
    void remove(int cadastro) {
        int ind = -1;
        for(int i = 0; i < size; i++) {
            if(heap[i].second == cadastro and ind == -1) {
                ind = i;
            }
        }
        swap(heap[ind], heap[size - 1]);
        size--;
        heap.pop_back();
        heapify(ind);
    }    
};

class hash_map {
    vector<vector<pair<int, int>>> table;
    static int mxsz;
    int size = 0;
    int get_hash(int key) {
        return key % mxsz;
    }
    int get(int key) {
        int hash = get_hash(key);
        for (int i = 0; i < table[hash].size(); i++) {
            if (table[hash][i].first == key) {
                return table[hash][i].second;
            }
        }
        return -1;
    }
public:
    hash_map() {
        table.resize(mxsz);
    }
    void insert(int key, int value) {
        int hash = get_hash(key);
        for (int i = 0; i < table[hash].size(); i++) {
            if (table[hash][i].first == key) {
                table[hash][i].second = value;
                return;
            }
        }
        table[hash].push_back({key, value});
        size++;
    }

    void erase(int key) {
        int hash = get_hash(key);
        for (int i = 0; i < table[hash].size(); i++) {
            if (table[hash][i].first == key) {
                swap(table[hash][i], table[hash][table[hash].size() - 1]);
                table[hash].pop_back();
                size--;
                return;
            }
        }
    }
    bool contains(int key) {
        int hash = get_hash(key);
        for (int i = 0; i < table[hash].size(); i++) {
            if (table[hash][i].first == key) {
                return true;
            }
        }
        return false;
    }
    int operator[](int key) {
        return get(key);
    }
};

