                int posicao = -1;
                for(int i = 1; i <= f; i++) {
                    pair<int, int> tmp = fileiras[i].top();
                    tmp.first = -tmp.first;
                    if(tmp.first < mn.first) {
                        mn = tmp;
                        posicao = i;
                    } else if(tmp.first == mn.first and tmp.second > mn.second) {
                        mn = tmp;
                        posicao = i;
                    }
                }
                bool inserir = true;
                if(p < mn.first) {
                    inserir = false;
                } else if(p == mn.first and cadastro_atual > mn.second) {
                    inserir = false;
                }
