                    fileira = posicao;
                    sem_assento.push({mn.first, -mn.second});
                    lugar.insert(mn.second, 0);
                    fileiras[posicao].pop();
                    fileiras[posicao].push({-p, cadastro_atual});
                    lugar.insert(cadastro_atual, posicao);
                }
            }
