          await easyRun(ns, "corporation/levelUpgrade", upgradable);
          availableFunds -= upgradeCost; // Update available funds
        }
      }
      lowestUpgradeCost = await getLeastExpensiveUpgrade();
    }
  }//---------------------------------------------------------------------------



