#include<stdio.h>
#include<string.h>

int change(char* s,int len,int m)//m
{
	int sum = 0;//
	for(int i = 0;i < m;i++)//
	{
		int hash[26] = {0};
		for(int t = i;t < len;t = t+m)//
		{
			int num = s[t]-'a';//num
			hash[num]++;//hash
		}
		int max=0;//hash
		for(int i = 0;i < 26;i++)
		{
			if(hash[i] > max) max = hash[i];
		}
		int ct = len/m-max;//i
		sum = sum+ct;//i
	}
	return sum;
}

bool is_repeated_string(char* s,int n) {
    
    // m1n
    for (int m = 1; m <= n / 2; m++) 
	{
        if (n % m == 0) // nm
		{  
            bool is_repeated = true;  // 
            // 
            for (int i = m; i < n; i++) {
                if (s[i] != s[i % m]) { 
                    // 
                    is_repeated = false;
                    break;
                }
            }
            if (is_repeated) {  // 
                return true;
            }
        }
    }
}

int main()
{
	char s[600];
	while(~scanf("%s",&s))
	{
		int n = strlen(s);
		//
		if(is_repeated_string(s,n)) printf("yes\n");
		else //m
		{
			int min,flag = 1;
			for (int m = 1; m <= n / 2; m++) 
			{
        		if (n % m == 0)
				{
					int now = change(s,n,m);
					if(flag)//
					{
						flag = 0;
						min = now;
					}
					else if(now < min) min = now;
				 } 
        	}
        	printf("No,at least %d",min);
		}
	}
}

#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>
#include <ctype.h>
#include <sys/stat.h>
#include <string.h>

int getFD(const char *const fname, int flags, int perm) {
  int fd = open(fname, flags, perm);
  if (fd < 0) {
    perror("");
    exit(1);
  }
  return fd;
}

void closeFD(int fd) {
  if (close(fd) < 0) {
    perror("");
    exit(1);
  }
}

int main(int argc, char *argv[]) {

  if (argc != 4) {
    fprintf(stderr, "Wrong usage!\n");
    exit(1);
  }

  int fd1 = getFD(argv[1], O_RDONLY, 0);

  int lower = 0, upper = 0, digits = 0, cnt = 0;
  char ch;
  while (read(fd1, &ch, 1)) {
    if (islower(ch)) {
      lower++;
    }
    if (isupper(ch)) {
      upper++;
    }
    if (isdigit(ch)) {
      digits++;
    }
    if (ch == argv[3][0]) {
      cnt++;
    }
  }

  int dim;
  struct stat buff;
  fstat(fd1, &buff);
  dim = buff.st_size;

  int fd2 = getFD(argv[2], O_RDWR | O_CREAT, S_IWUSR | S_IRUSR);
  
  char s[30];
  sprintf(s, "nr lower: %d\n", lower);
  write(fd2, s, strlen(s));
  sprintf(s, "nr upper: %d\n", upper);
  write(fd2, s, strlen(s));
  sprintf(s, "nr digits: %d\n", digits);
  write(fd2, s, strlen(s));
  sprintf(s, "nr %c: %d\n", argv[3][0], cnt);
  write(fd2, s, strlen(s));
  sprintf(s, "size: %d\n", dim);
  write(fd2, s, strlen(s));
  
  closeFD(fd1);
  closeFD(fd2);
  return 0;
}

