 /* checksum ripped and modified by me */
 
u_short
checksum (data, length)
	u_short *data;
	u_short length;
{

register long value;
u_short i;
    
      for (i = 0; i < (length >> 1); i++)
       value += data[i];
          
       if ((length & 1) == 1)
       value += (data[i] << 8);
                
       value = (value & 65535) + (value >> 16);
                  
       return (~value);
}
                    

