#include <bits/stdc++.h>
using namespace std;

typedef __int128 LL;

class Prime {
private:
	static LL multLL(LL a, LL b, LL mod) {
	    return a * b % mod;
	}

	static LL fastPowLL(LL res, LL n, LL mod) {
		LL ans = 1;
        while (n != 0) {
            if ((n & 1) == 1) {
				ans = multLL(ans, res, mod);
            }
			res = multLL(res, res, mod);
			n /= 2;
        }
		return ans % mod;
	}

public:
    static bool isPrimeLL(LL n) {
        if (n % 2 == 0) {
            return false;
        }
        LL d = n - 1;
        while (d % 2 == 0) {
            d /= 2;
        }
        static LL base[] = {2, 325, 9375, 28178, 450775, 9780504, 1795265022};
        for (int i = 0; i < 7; ++i) {
            if (n == base[i]) {
                return true;
            }
            LL t = d;
            LL y = fastPowLL(base[i], t, n);
            while (t != n - 1 && y != 1 && y != n - 1) {
                y = multLL(y, y, n);
                t <<= 1;
            }
            if (y != n - 1 && t % 2 == 0) {
                return false;
            }
        }
        return true;
    }
};


int main() {
