import chess

def simplify_fen_string(fen):
    parts = fen.split(' ')
    simplified_fen = ' '.join(parts[:4])  # ZachovÃ¡vÃ¡me pouze informace o pozici
    return simplified_fen

def evaluate_position(board):
    if board.is_checkmate():
        return -1000  # Mat protihrÃ¡Äi
    elif board.is_stalemate() or board.is_insufficient_material() or board.can_claim_draw():
        return 0  # RemÃ­za
    else:
        return None  # Hra pokraÄuje

def update_AR_for_mate_in_k(board, AR, max_k=1000):
    k = 0
    while True:
        k += 1
        changed = False
        for _ in range(2):  # DvakrÃ¡t pro kaÅ¾dou hodnotu k
            for fen in list(AR.keys()):
                board.set_fen(fen)
                if AR[fen] is not None:
                    continue  # Tato pozice jiÅ¾ byla ohodnocena

                evaluations = []
                for move in board.legal_moves:
                    board.push(move)
                    next_fen = simplify_fen_string(board.fen())
                    if next_fen not in AR:
                        AR[next_fen] = evaluate_position(board)  # PÅ™idÃ¡nÃ­ novÃ½ch pozic do AR
                    board.pop()

                    if AR[next_fen] is not None:
                        evaluations.append(AR[next_fen])

                # VÃ½poÄet novÃ©ho hodnocenÃ­ na zÃ¡kladÄ› nÃ¡sledovnÃ­kÅ¯
                if evaluations:
                    max_eval = max(evaluations)
                    if max_eval == -1000:
                        AR[fen] = 1000 - k
                        changed = True
                    elif 0 in evaluations:
                        AR[fen] = 0
                        changed = True

            if not changed:
                break  # Konec cyklu, pokud nedoÅ¡lo k Å¾Ã¡dnÃ© zmÄ›nÄ›

        if k >= max_k or not changed:
            break  # Konec hlavnÃ­ho cyklu, pokud dosÃ¡hneme max_k nebo nedoÅ¡lo k Å¾Ã¡dnÃ© zmÄ›nÄ›

            
def print_draw_positions(AR):
    """
    Vytiskne vÅ¡echny remÃ­zovÃ© pozice (hodnota 0) zaznamenanÃ© v slovnÃ­ku AR.
    """
    print("RemÃ­zovÃ© pozice:")
    for fen, value in AR.items():
        if value == 997:
            print(f"FEN: {fen}, Hodnota: {value}")
            
            
def main():
    initial_fen = "8/8/8/8/3Q4/5K2/8/4k3 w - - 0 1"
    board = chess.Board(initial_fen)
    AR = {simplify_fen_string(initial_fen): None}  # Inicializace AR s poÄÃ¡teÄnÃ­ pozicÃ­
    
    update_AR_for_mate_in_k(board, AR, max_k=50)  # Aktualizace AR
    print_draw_positions(AR)
    
