def binarnaPretraga(list, trazi): 
    pocetak = 0 
    kraj = len(list) - 1 
    srednji = 0 
    while pocetak <= kraj: 
        srednji = (pocetak + kraj) // 2 
        if list[srednji] < trazi: 
            pocetak = srednji + 1 
        elif list[srednji] > trazi: 
            kraj = srednji - 1
        else: 
            return srednji 
    return -1
