def insertSort(lista): 
    duzina_liste = len(lista) 
    for i in range(1, duzina_liste): 
        kljuc = lista[i] 
        j = i - 1  
        while j >= 0 and lista[j] > kljuc: 
            lista[j + 1] = lista[j] 
            j -= 1 
            
        lista[j + 1] = kljuc      

