def selection_sort(lista): 
    duzina_liste = len(lista) 
    for i in range(duzina_liste):
        min_index = i 
        for j in range(i + 1,duzina_liste): 
            if lista[j] < lista[min_index]: 
                min_index = j 
        lista[i],lista[min_index] = lista[min_index],lista[i]

