def bubblesort(lista):
    for n in range(len(lista)-1, 0, -1):
        zamenjen = False
        for i in range(n):
            if lista[i] > lista[i + 1]:
                zamenjen = True
                lista[i], lista[i + 1] = lista[i + 1],lista[i]
        if not zamenjen:
            return

