class Book{
private:
    string title;
    string author;
    double price;
public:
    static int totalSales;

    Book(const string &t="", const string &a="", const double &p=0):
    title(t), author(a), price(p){}

    Book(const Book &x):
    title(x.title), author(x.author), price(x.price){}

    Book& display(){
        cout<<"title: "<<title<<'\n';
        cout<<"author: "<<author<<'\n';
        cout<<"price: "<<price<<'\n';
        return *this;
    }

    Book& sell(){
        cout<<"Sell: "<<title<<'\n';
        totalSales+=1;
        return *this;
    }

    const string gettitle()const{
        return title;
    }
};

// You need to define the Bookstore class with its members, constructor, and methods
class Bookstore{
private:
    Book books[MAX_BOOKS];
    int num;
public:
    Bookstore():num(0){}

    Bookstore& addBook(Book book){
        for(int i=0;i<MAX_BOOKS;++i){
            if(books[i].gettitle()==""){
                books[i]=book;
                num++;
                return *this;
            }
        }
        cout<<"num of books is out of range\n";
        return *this;
    }

    Bookstore& displayBooks(){
        cout<<"Book list:\n";
        for(int i=0;i<num;++i){
            books[i].display();
            cout<<'\n';
        }
        return *this;
    }

    Bookstore& sellBook(int index){
        if(books[index].gettitle()==""){
            cout<<"index is not exist\n";
            return *this;
        }
        books[index].sell();
        books[index]=Book();
        num--;
        return *this;
    }
};

