#include<iostream>
#include<vector>
using namespace std;

class Account{
private:
	double balance;
public:
	Account(double b=0.0){
		if(b>=0.0)balance=b;
		else{
			cerr<<"Initial balance was invalid\n";
			 balance=0.0;
		}
	}

	virtual void credit(double x){
		balance+=x;
	}

	virtual bool  debit(double x){
		if(x>balance){
			cout<<"Debit amount exceeded account balance\n";
			return 0;
		}else balance-=x;
		return 1;
	}

	virtual double getBalance(){
		return balance;
	}
};

class SavingsAccount: public Account{
private:
	double interest_rate;
public:
	SavingsAccount(double b=0.0, double ir=0.0):Account(b){
		interest_rate = ir;
	}

	double calculateInterest(){
		return Account::getBalance()*interest_rate;
	}

};

class CheckingAccount: public Account{
private:
	double fee;
public:
	CheckingAccount(double b=0.0, double f=0.0):Account(b){
		fee = f;
	}

	virtual void credit(double x)override{
		Account::credit(x-fee);
	}

	virtual bool debit(double x)override{
		if(Account::debit(x+fee))return 1;
		else return 0;
	}
};

int main() {
    vector<Account*> accounts;

    // Create SavingsAccount and CheckingAccount objects
    accounts.push_back(new SavingsAccount(500.0, 0.05)); // Savings account with $500 initial balance and 5% interest rate
    accounts.push_back(new CheckingAccount(200.0, 1.0)); // Checking account with $200 initial balance and $1 fee per transaction
	
    // Process each account
	//SavingsAccount* saptr = dynamic_cast<SavingsAccount*> (accounts[0]);
	SavingsAccount* saptr = dynamic_cast<SavingsAccount*>(accounts[0]);
	cout<<"Processing 14SavingsAccount account.\n";
	int deposit, withdraw;
	cout<<"Enter amount to deposit:";
	cin>>deposit;
	saptr->credit(deposit);
	cout<<"Enter amount to withdraw:";
	cin>>withdraw;
	saptr->debit(withdraw);
	double x=saptr->calculateInterest();
	cout<<"Interest added:"<<x<<'\n';
	saptr->credit(x);
	cout<<"Updated balance:";
	cout<<saptr->getBalance()<<'\n';

	CheckingAccount* captr = dynamic_cast<CheckingAccount*>(accounts[1]);
	cout<<"Processing 15CheckingAccount account.\n";
	cout<<"Enter amount to deposit:";
	cin>>deposit;
	captr->credit(deposit);
	cout<<"Enter amount to withdraw:";
	cin>>withdraw;
	captr->debit(withdraw);
	cout<<"Updated balance:";
