        for (auto i = 0; i < Tile::SIZE; ++i){
            for (auto j = 0; j < Tile::SIZE; ++j){
                PRACTIKUM_ASSERT_EQUAL(tile.GetPixel({i, j}), color);
            }
        }
    }

void TileGetPixel(){
