      obj = &objs[i];
      if(!obj->in_front /*&&*/ TYPE_CHECK)
        scene_changed |= obj->update(obj, render_only);

    }


  } else {
    for(u32 i=0; i<objs_len; ++i){
      obj = &objs[i];
      if(obj->in_front /*&&*/ TYPE_CHECK)
        scene_changed |= obj->update(obj, render_only);

    }

  }

  #undef TYPE_CHECK



  if(!render_only) return scene_changed;
  else             return false;

}





