	if (gameStarted && (millis() - startTime >= timer)) { // if the timer has run out
		lcd.clear(); // clear the LCD
		lcd.print("Time's Up!"); // print "Time's Up!" on the LCD
		gameStarted = false; // set gameStarted to false to start a new game
	}
	// USER CODE END
}

