#include <iostream>
#include <vector>
#include <queue>
#include <map>
using namespace std;
typedef long long ll;

int main() {
    ios_base::sync_with_stdio(false);
    ll k, x;
    cin >> k >> x;
    
    ll res = 1e18;
    if(x % k == 0) {
        res = x / k;
    }
    
    ll power = 1;
    for(int i = 0; i < 100; i++) {
        ll tmp = power * k;
        
        if(tmp <= x) {
            if(x % tmp == 0) {
                ll n = x / tmp;
                if(n * power * k == x) {
                    res = min(res, n + i);
                }
            }
        }
        else {
            break;
        }
        power *= k;
    }
    
    if(res == 1e18) {
