function solve() {
    let daysInput = document.getElementById('days-input');
    let hoursInput = document.getElementById('hours-input');
    let minutesInput = document.getElementById('minutes-input');
    let secondsInput = document.getElementById('seconds-input');
    
    let daysBtn = document.getElementById('daysBtn');
    let hoursBtn = document.getElementById('hoursBtn');
    let minutesBtn = document.getElementById('minutesBtn');
    let secondsBtn = document.getElementById('secondsBtn');
    
    daysBtn.addEventListener('click', convertFromDays);
    hoursBtn.addEventListener('click', convertFromHours);
    minutesBtn.addEventListener('click', convertFromMinutes);
    secondsBtn.addEventListener('click', convertFromSeconds);
    
    function convertFromDays(e) {
        e.preventDefault();
        let inputDays = Number(daysInput.value);
        hoursInput.value = (inputDays * 24).toFixed(2);
        minutesInput.value = (inputDays * 1440).toFixed(2);
        secondsInput.value = (inputDays * 86400).toFixed(2);
    }
    
    function convertFromHours(e) {
        e.preventDefault();
        let inputHours = Number(hoursInput.value);
        daysInput.value = (inputHours / 24).toFixed(2);
        minutesInput.value = (inputHours * 60).toFixed(2);
        secondsInput.value = (inputHours * 3600).toFixed(2);
    }
    
    function convertFromMinutes(e) {
        e.preventDefault();
        let inputMinutes = Number(minutesInput.value);
        daysInput.value = (inputMinutes / 1440).toFixed(2);
        hoursInput.value = (inputMinutes / 60).toFixed(2);
        secondsInput.value = (inputMinutes * 60).toFixed(2);
    }
    
    function convertFromSeconds(e) {
        e.preventDefault();
        let inputSeconds = Number(secondsInput.value);
        daysInput.value = (inputSeconds / 86400).toFixed(2);
        hoursInput.value = (inputSeconds / 3600).toFixed(2);
        minutesInput.value = (inputSeconds / 60).toFixed(2);
    }
}

