            cin >> matrix[i][j];
        }
    }
    int maxGold = 0;
    int maxInAPath = 0;
    for(int i = 0; i < n; i++){
        for(int j = 0; j < m; j++){
            if(matrix[i][j] != 0){
                // maxGold += getGold(i, j, matrix);
                int recGold = 0;
                recGold = getGold(i, j, matrix);
                maxGold += recGold;
                maxInAPath = max(maxInAPath, recGold);
            }
        }
    }
    cout << maxGold << '\n';
    cout << maxInAPath << '\n';
