		cin >> a;
		if(a==1){
			cin >> b;
			//cout << "b: " << b << '\n';
			heap.push_back(b);
			up_heap(heap.size()-1);
		}else if(a==2){
			cout << heap[0] << '\n';
		}else if(a==3){
			del();
		}
	}
	//cout << heap.size() << '\n';
	//print_heap();
	//del();
	//cout << "========" << '\n';
	//cout << heap.size() << '\n';
	//print_heap();
	
	return 0;
}

