    console.log("Error: ", error);
    return ret;
  }
  if (lastip == null || lastip.timestamp != result.timestamp) {
    lastip = result;
    ret = true;
  }

  return ret;
}

async function meterload() {
  let url = environment.base_url + "/" + "?do=api&fn=lastmeter&device=" + get['id'];
