  for (let i = 0; i < 3; i++) {
    let plet = String.fromCharCode("A".charCodeAt() + i);
    let phst = state.Power & (1 << i);
    aphst[i] = phst;
    let phasecolor = phst ? greenColor : errorColor;
    $(`#phase_${plet}`).css("stroke", phasecolor);
//V11
    $("#Volt" + i).text(plet);
    if (state.PhaseV) {
      let aVolt = JSON.parse(state.PhaseV);
      if (aVolt[i] !== null && aVolt[i] > 1)
        $("#Volt" + i).text(aVolt[i]);
    }
  }
  //$(".phase").css("stroke",phasecolor);


// ĞšĞĞĞ¢ĞĞšĞ¢ĞĞ Ğ«
  for (let i = 1; i <= 4; i++) {
    let swtrouble = state.Trouble & (1 << (i - 1)); 	//Ğ°Ğ²Ğ°Ñ€Ğ¸Ğ¹Ğ½Ğ¾ÑÑ‚ÑŒ ĞºĞ¾Ğ½Ñ‚Ğ°ĞºÑ‚Ğ¾Ñ€Ğ°
    let altsw = state.Trouble & (1 << (i + 27)); 		//Ğ°Ğ»ÑŒÑ‚ĞµÑ€Ğ½Ğ°Ñ‚Ğ¸Ğ²Ğ½Ğ¾Ğµ Ğ²ĞºĞ»ÑÑ‡ĞµĞ½Ğ¸Ğµ ĞºĞ¾Ğ½Ñ‚Ğ°ĞºÑ‚Ğ¾Ñ€Ğ°
    let on = (onoff[i - 1] !== '0'); 					// Ğ·Ğ½Ğ°Ñ‡ĞµĞ½Ğ¸Ğµ ÑƒĞ¿Ñ€Ğ°Ğ²Ğ»ĞµĞ½Ğ¸Ñ on/off && aphst[0]; 20.07.21 - remove Phase A falsification
    // ĞšĞ¾Ğ¼Ğ°Ğ½Ğ´Ğ° Ğ½Ğ° Ğ²ĞºĞ»ÑÑ‡ĞµĞ½Ğ¸Ğµ ON
    if (on) {
      $(`.S${i}_on`).attr("visibility", "visible");		//Ğ²Ğ¸Ğ´Ğ¸Ğ¼Ğ¾ÑÑ‚ÑŒ ÑĞ¾ÑÑ‚Ğ¾ÑĞ½Ğ¸Ñ on
      $(`.S${i}_off`).attr("visibility", "hidden");		//ÑĞºÑ€Ñ‹Ñ‚ÑŒ ÑĞ¾ÑÑ‚Ğ¾ÑĞ½Ğ¸Ğµ off
      $(`.S${i}_onoff`).attr("visibility", "hidden");	//ÑĞºÑ€Ñ‹Ñ‚ÑŒ ÑĞ¾ÑÑ‚Ğ¾ÑĞ½Ğ¸Ğµ onoff
      $(`.S${i}_offon`).attr("visibility", "hidden");	//ÑĞºÑ€Ñ‹Ñ‚ÑŒ ÑĞ¾ÑÑ‚Ğ¾ÑĞ½Ğ¸Ğµ offon
      let swc = "green";
      $(`#S${i}_rect1`).css("fill", "#AFEEEE");			// Ğ»ĞµĞ²Ñ‹Ğ¹ Ğ¿Ñ€ÑĞ¼Ğ¾ÑƒĞ³Ğ¾Ğ»ÑŒĞ½Ğ¸Ğº (Ğ³Ğ¾Ğ»ÑƒĞ±Ğ¾Ğ¹)
