    <style type="text/css">
        .content-row {
            overflow: hidden;
            transition: all 0.3s ease;
            max-height: 0;
            opacity: 0;
        }
        .content-row.open {
            max-height: 500px;
            opacity: 1;
        }
        
        .toast {
            animation: fade-in 0.3s ease-out;
        }
        
        .toast.hide {
            animation: fade-out 0.3s ease-out;
            opacity: 0;
        }
        
        /* –Ø–≤–Ω–æ –∑–∞–¥–∞–µ–º —Å—Ç–∏–ª–∏ –¥–ª—è —Å–∫—Ä—ã—Ç—ã—Ö —Å—Ç—Ä–æ–∫ */
        tr.content-row {
            display: none;
            opacity: 0;
            transition: opacity 0.3s ease;
        }
        
        tr.content-row.open {
            display: table-row;
            opacity: 1;
        }
        
        /* –°—Ç–∏–ª–∏ –¥–ª—è –º–æ–¥–∞–ª—å–Ω–æ–≥–æ –æ–∫–Ω–∞ */
        .modal {
