            transform: translateY(-20px);
            opacity: 0;
        }
        
        .modal.open {
            transform: translateY(0);
            opacity: 1;
        }
        
        .modal-backdrop {
            transition: opacity 0.3s ease;
            opacity: 0;
        }
        
        .modal-backdrop.open {
            opacity: 1;
        }
        
        .volume-control-group:hover .volume-slider {
            opacity: 1;
            visibility: visible;
            transform: scale(1);
        }
        
        .volume-slider {
            transition: opacity 0.2s ease, visibility 0.2s ease, transform 0.2s ease;
        }
    </style>
    <style>
