/*comment*/
.comment-form .form-group {
    display: flex;
    align-items: center;
    gap: 10px;
    margin-top: 10px;
}

.comment-form textarea {
    flex: 1;
    resize: none;
    padding: 8px;
    font-size: 14px;
    border-radius: 6px;
    border: 1px solid #ccc;
    margin-left: 5px;
}

.comment-button, .likes_logic button {
    background-color: #3498db;
    color: white;
    border: none;
    padding: 4px 12px;
    border-radius: 6px;
    font-weight: bold;
    cursor: pointer;
    transition: background-color 0.3s ease;
    margin-right: 5px;
}

.comment-button:hover {
    background-color: #2980b9;
}

.likes_logic {
    display: flex;
    flex-direction: column;
    padding-left: 15px;
}

.all-comments .comment-info {
    color: white;
}

