	public static List<Integer> list = new ArrayList<>();

    public static int s(int n){
        while (list.size() <= n) {
            list.add(-1); // initialize with a dummy value
        }

        if(n<=2){
            list.set(n,1);
            return 1;
        }

        if(list.get(n) != -1){
            return list.get(n);
        }

        int result = s(n-1)+s(n-2);
        list.set(n,result);
        return result;
    }

import asyncio
import json

import niquests


async def get() -> dict:
    async with niquests.AsyncSession() as session:
        response = await session.get(
            "https://stream.wikimedia.org/v2/stream/recentchange", stream=True
        )
        async for chunk in await response.iter_content():
            try:
                data = chunk.decode()
            except UnicodeDecodeError:
                continue

            if data.startswith("data:"):
                try:
                    # it may fail here
                    result = json.loads(data.removeprefix("data:"))
                    # if it fails, it does not return
                    # instead iterate
                    return result
                except json.JSONDecodeError:
                    # error
                    pass


async def main():
    try:
        return await asyncio.wait_for(get(), 60)
    except asyncio.TimeoutError:
        print("Got a timeout")


asyncio.run(main())

