    printf("This is a simple program to check Palindrome Number.\n\n");
    printf("Enter a number : ");
    scanf("%ld", &num);

    bnum = num;
    rev = 0;

    while(num != 0)
    {
        rem = num % 10;
        rev = rev * 10 + rem;
        num = num / 10;
    }

    if(bnum == rev)
    {
        printf("%ld - is a Palindrome Number.", bnum);
    }
    else
    {
        printf("%ld - is not a Palindrome Number.", bnum);
    }
}

