        </button>

        {/* Folder Icon */}
        <div className="flex-shrink-0">
          {isExpanded ? (
            <FaFolderOpen className="w-5 h-5 text-blue-500" />
          ) : (
            <FaFolder className="w-5 h-5 text-blue-600" />
          )}
        </div>

        {/* Collection Name */}
        <span
          {...attributes}
          {...listeners}
          className="flex-1 font-medium text-gray-800 cursor-grab active:cursor-grabbing"
        >
          {collection.name}
        </span>

        {/* Collection Info */}
        <div className="flex items-center gap-2 text-sm text-gray-500">
          {hasChildren && (
            <span className="bg-blue-100 text-blue-700 px-2 py-1 rounded-full text-xs">
              {collection.children.length} collections
            </span>
          )}
          {hasProducts && (
            <span className="bg-green-100 text-green-700 px-2 py-1 rounded-full text-xs">
              {collection.directProducts.length} products
            </span>
          )}
        </div>

        {/* Move Button */}
        {!collection.isRoot && (
          <button
            onClick={(e) => {
              e.stopPropagation();
              onMoveCollection(collection);
            }}
            className="p-1 hover:bg-gray-200 rounded opacity-0 group-hover:opacity-100 transition-opacity"
            title="Move collection"
          >
            <LuMove className="w-4 h-4 text-gray-600" />
          </button>
        )}
      </div>

      {/* Expanded Content */}
      {isExpanded && (
        <div className="ml-6">
          {/* Child Collections */}
          {hasChildren && (
            <SortableContext
              items={collection.children}
              strategy={verticalListSortingStrategy}
            >
              <div className="space-y-1">
                {collection.children.map((childId) => (
                  <SortableCollectionItem
                    key={childId}
                    collection={collections[childId]}
                    collections={collections}
                    expandedCollections={expandedCollections}
                    onToggleExpand={onToggleExpand}
                    onMoveCollection={onMoveCollection}
                    onCollectionClick={onCollectionClick}
                    selectedCollectionId={selectedCollectionId}
                    level={level + 1}
                  />
                ))}
              </div>
            </SortableContext>
          )}

          {/* Direct Products */}
          {hasProducts && (
            <div className="mt-2 space-y-1">
              {collection.directProducts.map((productId) => (
                <div
                  key={productId}
                  className="flex items-center gap-2 p-2 rounded-lg hover:bg-gray-50"
                  style={{ paddingLeft: `${(level + 1) * 24 + 8}px` }}
                >
                  <div className="w-4 h-4" /> {/* Spacer for alignment */}
                  <LuPackage className="w-4 h-4 text-orange-500 flex-shrink-0" />
                  <span className="text-gray-700">
                    {productDatabase[productId]?.name || productId}
                  </span>
                  <span className="text-xs text-gray-400 bg-gray-100 px-2 py-1 rounded">
                    Product
                  </span>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

function MoveCollectionModal({
  isOpen,
  onClose,
  collection,
  collections,
  onMoveCollection,
}) {
  const [selectedParentId, setSelectedParentId] = useState(
    collection?.parentId || "root"
  );

  if (!isOpen || !collection) return null;

  const buildCollectionTree = (parentId, level = 0) => {
    const children = Object.values(collections).filter(
      (c) => c.parentId === parentId && c.id !== collection.id
    );

    return children.map((child) => (
      <div key={child.id}>
        <div
          className={`flex items-center gap-2 p-2 rounded cursor-pointer hover:bg-gray-50 ${
            selectedParentId === child.id
              ? "bg-blue-50 border border-blue-200"
              : ""
          }`}
          style={{ paddingLeft: `${level * 20}px` }}
          onClick={() => setSelectedParentId(child.id)}
        >
          <FaFolder className="w-4 h-4 text-blue-500" />
          <span className="text-sm">{child.name}</span>
        </div>
        {buildCollectionTree(child.id, level + 1)}
      </div>
    ));
  };

  const handleMove = () => {
    onMoveCollection(collection.id, selectedParentId);
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-96 max-h-96 overflow-hidden">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold">Move Collection</h3>
          <button onClick={onClose} className="p-1 hover:bg-gray-200 rounded">
            <FaXmark className="w-5 h-5" />
          </button>
        </div>

        <p className="text-sm text-gray-600 mb-4">
          Move "{collection.name}" to a new parent collection:
        </p>

        <div className="border rounded-lg p-3 max-h-48 overflow-y-auto mb-4">
          {/* Root Option */}
          <div
            className={`flex items-center gap-2 p-2 rounded cursor-pointer hover:bg-gray-50 ${
              selectedParentId === "root"
                ? "bg-blue-50 border border-blue-200"
                : ""
            }`}
            onClick={() => setSelectedParentId("root")}
          >
            <FaFolderOpen className="w-4 h-4 text-blue-500" />
            <span className="text-sm font-medium">Root</span>
          </div>

          {/* Collection Tree */}
          {buildCollectionTree("root", 1)}
        </div>

        <div className="flex gap-3">
          <button
            onClick={onClose}
            className="flex-1 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
          >
            Cancel
          </button>
          <button
            onClick={handleMove}
            className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            disabled={selectedParentId === collection.parentId}
          >
            Move Here
          </button>
        </div>
      </div>
    </div>
  );
}

export default function CollectionManagementSystem() {
  const [collections, setCollections] = useState(initialCollections);
  const [expandedCollections, setExpandedCollections] = useState({
    root: true,
    electronics: true,
  });
  const [moveModalOpen, setMoveModalOpen] = useState(false);
  const [selectedCollection, setSelectedCollection] = useState(null);
  const [selectedCollectionId, setSelectedCollectionId] = useState(null);
  const [activeId, setActiveId] = useState(null);

  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  const handleCollectionClick = useCallback((collectionId) => {
    setSelectedCollectionId(collectionId);
  }, []);

  const handleToggleExpand = useCallback((collectionId) => {
    setExpandedCollections((prev) => ({
      ...prev,
      [collectionId]: !prev[collectionId],
    }));
  }, []);

  const handleMoveCollection = useCallback((collection) => {
    setSelectedCollection(collection);
    setMoveModalOpen(true);
  }, []);

  const handleMoveConfirm = useCallback((collectionId, newParentId) => {
    setCollections((prev) => {
      const newCollections = { ...prev };
      const collection = newCollections[collectionId];
      const oldParent = newCollections[collection.parentId];
      const newParent = newCollections[newParentId];

      // Remove from old parent
      if (oldParent) {
        oldParent.children = oldParent.children.filter(
          (id) => id !== collectionId
        );
      }

      // Add to new parent
      if (newParent) {
        newParent.children = [...newParent.children, collectionId];
      }

      // Update collection's parent
      collection.parentId = newParentId;

      return newCollections;
    });
  }, []);

  const handleDragStart = (event) => {
    setActiveId(event.active.id);
  };

  const handleDragEnd = (event) => {
    const { active, over } = event;
    setActiveId(null);

    if (!over || active.id === over.id) return;

    setCollections((prev) => {
      const newCollections = { ...prev };
      const activeCollection = newCollections[active.id];
      const overCollection = newCollections[over.id];

      // Only allow reordering within the same parent
      if (activeCollection.parentId !== overCollection.parentId) {
        return prev;
      }

      const parent = newCollections[activeCollection.parentId];
      const oldIndex = parent.children.indexOf(active.id);
      const newIndex = parent.children.indexOf(over.id);

      // Reorder children array
      const newChildren = [...parent.children];
      newChildren.splice(oldIndex, 1);
      newChildren.splice(newIndex, 0, active.id);
      parent.children = newChildren;

      return newCollections;
    });
  };

  const rootCollection = collections.root;

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-gray-900 mb-2">
          Collection Management
        </h1>
        <p className="text-gray-600">
          Manage your product collections with drag-and-drop reordering and
          hierarchical organization.
        </p>
      </div>

      <div className="bg-white rounded-lg border border-gray-200 p-4">
        <DndContext
          sensors={sensors}
          collisionDetection={closestCenter}
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
        >
          <div className="space-y-1">
            {/* Root Collections */}
            <SortableContext
              items={rootCollection.children}
              strategy={verticalListSortingStrategy}
            >
              {rootCollection.children.map((childId) => (
                <SortableCollectionItem
                  key={childId}
                  collection={collections[childId]}
                  collections={collections}
                  expandedCollections={expandedCollections}
                  onToggleExpand={handleToggleExpand}
                  onMoveCollection={handleMoveCollection}
                  onCollectionClick={handleCollectionClick}
                  selectedCollectionId={selectedCollectionId}
                  level={0}
                />
              ))}
            </SortableContext>
          </div>

          <DragOverlay>
            {activeId ? (
              <div className="bg-white border border-gray-300 rounded-lg shadow-lg p-2 opacity-95">
                <div className="flex items-center gap-2">
                  <FaFolder className="w-5 h-5 text-blue-600" />
                  <span className="font-medium text-gray-800">
                    {collections[activeId]?.name}
                  </span>
                </div>
              </div>
            ) : null}
          </DragOverlay>
        </DndContext>
      </div>

      {/* Selected Collection Products */}
      {selectedCollectionId && (
        <div className="mt-6 bg-white rounded-lg border border-gray-200 p-4">
          <div className="mb-4">
            <h2 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
              <LuPackage className="w-5 h-5 text-orange-500" />
              Products in "{collections[selectedCollectionId]?.name}"
            </h2>
            <p className="text-sm text-gray-600 mt-1">
              {collections[selectedCollectionId]?.directProducts?.length || 0}{" "}
              products in this collection
            </p>
          </div>

          {collections[selectedCollectionId]?.directProducts?.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {collections[selectedCollectionId].directProducts.map(
                (productId) => (
                  <div
                    key={productId}
                    className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow"
                  >
                    <div className="flex items-center gap-3">
                      <div className="w-12 h-12 bg-orange-100 rounded-lg flex items-center justify-center">
                        <LuPackage className="w-6 h-6 text-orange-600" />
                      </div>
                      <div className="flex-1">
                        <h3 className="font-medium text-gray-900">
                          {productDatabase[productId]?.name || productId}
                        </h3>
                        <p className="text-sm text-gray-500">ID: {productId}</p>
                      </div>
                    </div>
                  </div>
                )
              )}
            </div>
          ) : (
            <div className="text-center py-8">
              <LuPackage className="w-12 h-12 text-gray-300 mx-auto mb-3" />
              <h3 className="text-lg font-medium text-gray-500 mb-1">
                No Products
              </h3>
              <p className="text-sm text-gray-400">
                This collection doesn't have any direct products yet.
              </p>
            </div>
          )}
        </div>
      )}

      <MoveCollectionModal
        isOpen={moveModalOpen}
        onClose={() => setMoveModalOpen(false)}
        collection={selectedCollection}
        collections={collections}
        onMoveCollection={handleMoveConfirm}
      />
    </div>
  );
}

