        public TripleAttackFighter(string name, int damage, int defense, int health)
            : base("Ð¢Ñ€Ð¾Ð¹Ð½Ð°Ñ ÐÑ‚Ð°ÐºÐ°", name, damage, defense, health, "ÐšÐ°Ð¶Ð´ÑƒÑŽ Ñ‚Ñ€ÐµÑ‚ÑŒÑŽ Ð°Ñ‚Ð°ÐºÑƒ Ð½Ð°Ð½Ð¾ÑÐ¸Ñ‚ Ð´Ð²Ð¾Ð¹Ð½Ð¾Ð¹ ÑƒÑ€Ð¾Ð½") { }

        public override void Attack(Fighter opponent)
