        public RageFighter(string name, int damage, int defense, int health)
            : base("Ð¯Ñ€Ð¾ÑÑ‚ÑŒ", name, damage, defense, health, "ÐŸÐ¾Ð»ÑƒÑ‡Ð°Ñ ÑƒÑ€Ð¾Ð½ Ð½Ð°ÐºÐ°Ð¿Ð»Ð¸Ð²Ð°ÐµÑ‚ ÑÑ€Ð¾ÑÑ‚ÑŒ Ð¸ Ð»ÐµÑ‡Ð¸Ñ‚ÑÑ") { }

        public override void TakeDamage(int damage)
