    class Arena
    {
        private List<Fighter> availableFighters = new List<Fighter>();

        public Arena()
        {
            availableFighters.AddRange(new Fighter[]
            {
                new DoubleDamageFighter("TempleKnight", 15, 5, 100),
                new TripleAttackFighter("Knight", 12, 7, 110),
                new RageFighter("Rager", 10, 8, 120),
                new MageFighter("Sorcerer", 18, 3, 90),
                new DodgeFighter("Dodger", 11, 6, 105),
                new BasicFighter("Warrior", 13, 6, 115)
            });
        }

        public void Run()
        {
            const string CommandFight = "go";
            const string CommandExitMenu = "exit";

            bool isProgramUp = true;

            while (isProgramUp)
            {
                Console.WriteLine("Ð”Ð¾Ð±Ñ€Ð¾ Ð¿Ð¾Ð¶Ð°Ð»Ð¾Ð²Ð°Ñ‚ÑŒ Ð½Ð° Ð°Ñ€ÐµÐ½Ñƒ ÐšÐ¾Ð»Ð¸Ð·ÐµÑ!");
                Console.WriteLine($"\nÐ’Ñ‹Ð±ÐµÑ€Ð¸Ñ‚Ðµ Ð´ÐµÐ¹ÑÑ‚Ð²Ð¸Ðµ: \n1) Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ '{CommandFight}' Ñ‡Ñ‚Ð¾Ð±Ñ‹ Ð¿Ð¾ÑÐ¼Ð¾Ñ‚Ñ€ÐµÑ‚ÑŒ Ð±Ð¾Ð¹ " +
                                  $"\n2) Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ '{CommandExitMenu}' Ñ‡Ñ‚Ð¾Ð±Ñ‹ Ð²Ñ‹Ð¹Ñ‚Ð¸");

                string userInput = Console.ReadLine();

                switch (userInput)
                {
                    case CommandFight:
                        ConductFight();
                        break;

                    case CommandExitMenu:
                        isProgramUp = false;
                        break;

                    default:
                        Console.WriteLine("ÐÐµÐ²ÐµÑ€Ð½Ð°Ñ ÐºÐ¾Ð¼Ð°Ð½Ð´Ð°!!!");
                        break;
                }

                Console.ReadKey();
                Console.Clear();
            }
        }

        private void ConductFight()
        {
            Console.WriteLine("\nÐ¡Ð¿Ð¸ÑÐ¾Ðº Ð´Ð¾ÑÑ‚ÑƒÐ¿Ð½Ñ‹Ñ… Ð±Ð¾Ð¹Ñ†Ð¾Ð²:");
            for (int i = 0; i < availableFighters.Count; i++)
            {
                Console.WriteLine($"{i + 1}. {availableFighters[i].Name} ({availableFighters[i].Type}) {availableFighters[i].GetShortStats()}");
            }

            Fighter fighter1 = ChooseFighter("Ð¿ÐµÑ€Ð²Ð¾Ð³Ð¾");
            if (fighter1 == null) return;

            Fighter fighter2 = ChooseFighter("Ð²Ñ‚Ð¾Ñ€Ð¾Ð³Ð¾");
            if (fighter2 == null) return;

            StartFight(fighter1, fighter2);
        }

        private Fighter ChooseFighter(string ordinalNumber)
        {
            while (true)
            {
                Console.Write($"Ð’Ñ‹Ð±ÐµÑ€Ð¸Ñ‚Ðµ Ð¿Ð¾Ñ€ÑÐ´ÐºÐ¾Ð²Ñ‹Ð¹ Ð½Ð¾Ð¼ÐµÑ€ {ordinalNumber} Ð±Ð¾Ð¹Ñ†Ð°: ");
                if (int.TryParse(Console.ReadLine(), out int fighterNumber))
                {
                    if (fighterNumber >= 1 && fighterNumber <= availableFighters.Count)
                    {
                        return CreateFighterCopy(availableFighters[fighterNumber - 1]);
                    }
                    else
                    {
                        Console.WriteLine("ÐÐµÐ²ÐµÑ€Ð½Ñ‹Ð¹ Ð½Ð¾Ð¼ÐµÑ€ Ð±Ð¾Ð¹Ñ†Ð°. ÐŸÐ¾Ð¶Ð°Ð»ÑƒÐ¹ÑÑ‚Ð°, Ð²Ñ‹Ð±ÐµÑ€Ð¸Ñ‚Ðµ Ð½Ð¾Ð¼ÐµÑ€ Ð¸Ð· ÑÐ¿Ð¸ÑÐºÐ°.");
                    }
                }
                else
                {
                    Console.WriteLine("ÐÐµÐ²ÐµÑ€Ð½Ñ‹Ð¹ Ð²Ð²Ð¾Ð´. ÐŸÐ¾Ð¶Ð°Ð»ÑƒÐ¹ÑÑ‚Ð°, Ð²Ð²ÐµÐ´Ð¸Ñ‚Ðµ Ñ‡Ð¸ÑÐ»Ð¾.");
                }
            }
        }

        private Fighter CreateFighterCopy(Fighter fighter)
        {
            if (fighter is DoubleDamageFighter)
                return new DoubleDamageFighter(fighter.Name, fighter.Damage, fighter.Defense, fighter.Health);
            if (fighter is TripleAttackFighter)
                return new TripleAttackFighter(fighter.Name, fighter.Damage, fighter.Defense, fighter.Health);
            if (fighter is RageFighter)
                return new RageFighter(fighter.Name, fighter.Damage, fighter.Defense, fighter.Health);
            if (fighter is MageFighter)
                return new MageFighter(fighter.Name, fighter.Damage, fighter.Defense, fighter.Health);
            if (fighter is DodgeFighter)
                return new DodgeFighter(fighter.Name, fighter.Damage, fighter.Defense, fighter.Health);
            if (fighter is BasicFighter)
                return new BasicFighter(fighter.Name, fighter.Damage, fighter.Defense, fighter.Health);

            return null;
        }

        private void StartFight(Fighter fighter1, Fighter fighter2)
        {
            Console.WriteLine("\n=== ÐÐ°Ñ‡Ð¸Ð½Ð°ÐµÑ‚ÑÑ Ð±Ð¾Ð¹! ===");
            Console.WriteLine($"Ð‘Ð¾ÐµÑ† 1: {fighter1.Name} ({fighter1.Type})");
            Console.WriteLine($"Ð‘Ð¾ÐµÑ† 2: {fighter2.Name} ({fighter2.Type})");

            int round = 1;

            while (fighter1.IsAlive() && fighter2.IsAlive())
            {
                Console.WriteLine($"\n--- Ð Ð°ÑƒÐ½Ð´ {round} ---");

                fighter1.Attack(fighter2);
                if (!fighter2.IsAlive())
                {
                    Console.WriteLine($"{fighter2.Name} Ð¿Ð¾Ð²ÐµÑ€Ð¶ÐµÐ½!");
                    break;
                }

                fighter2.Attack(fighter1);
                if (!fighter1.IsAlive())
                {
                    Console.WriteLine($"{fighter1.Name} Ð¿Ð¾Ð²ÐµÑ€Ð¶ÐµÐ½!");
                    break;
                }

                Console.WriteLine($"\nÐ¡Ñ‚Ð°Ñ‚Ð¸ÑÑ‚Ð¸ÐºÐ° Ð¿Ð¾ÑÐ»Ðµ Ñ€Ð°ÑƒÐ½Ð´Ð° {round}:");
                Console.WriteLine($"  {fighter1.Name}: Ð—Ð´Ð¾Ñ€Ð¾Ð²ÑŒÐµ = {fighter1.Health}");
                Console.WriteLine($"  {fighter2.Name}: Ð—Ð´Ð¾Ñ€Ð¾Ð²ÑŒÐµ = {fighter2.Health}");

                Console.WriteLine("\nÐÐ°Ð¶Ð¼Ð¸Ñ‚Ðµ Ð»ÑŽÐ±ÑƒÑŽ ÐºÐ»Ð°Ð²Ð¸ÑˆÑƒ Ð´Ð»Ñ Ð¿Ñ€Ð¾Ð´Ð¾Ð»Ð¶ÐµÐ½Ð¸Ñ...");
                Console.ReadKey();
                Console.Clear();
                round++;
            }

            Console.WriteLine("\n=== Ð‘Ð¾Ð¹ Ð¾ÐºÐ¾Ð½Ñ‡ÐµÐ½! ===");
            if (fighter1.IsAlive())
            {
                Console.WriteLine($"ÐŸÐ¾Ð±ÐµÐ´Ð¸Ð» {fighter1.Name}!");
            }
            else if (fighter2.IsAlive())
            {
                Console.WriteLine($"ÐŸÐ¾Ð±ÐµÐ´Ð¸Ð» {fighter2.Name}!");
            }
            else
            {
                Console.WriteLine("ÐÐ¸Ñ‡ÑŒÑ!");
            }

            Console.ReadKey();
        }
    }

