#include<bits/stdc++.h>
using namespace std;
#define ll long long
#define nl "\n"
#define YES cout << "YES\n"
#define NO cout << "NO\n"
#define db cout << "h"
#define pb push_back
#define llmx LONG_LONG_MAX
#define llmn LONG_LONG_MIN
#define mod 1000000007
const int N = 1e5 + 7;
const int INF = 1e9 + 10;

ll f(ll i, vector<ll> &dp, vector<ll> &arr) {
    if (i == 0) return 0;
    if (i == 1) return arr[i];

    if (dp[i] != -1) return dp[i];

    ll way1 = f(i - 1, dp, arr) + arr[i] - 1;
    ll way2 = f(i - 2, dp, arr) + arr[i - 1] + max(0LL, arr[i] - (i - 1));
    return dp[i] = min(way1, way2);
}

void solve(){
    ll n;
    cin >> n;
    vector<ll> arr(n + 1);
    for (int i = 1; i <= n; i++) cin >> arr[i];
    
    vector<ll> dp(n + 1, -1);
    // dp[0] = 0;
    // dp[1] = arr[1];
