    //     ll way1 = dp[i - 1] + arr[i] - 1;
    //     ll way2 = dp[i - 2] + arr[i - 1] + max(0LL, arr[i] - (i - 1));
    //     dp[i] = min(way1, way2);
    // }
    cout << f(n, dp, arr) << nl;
}

