    // public int lengthOfLongestSubstring(String s) {
    //     Set<Character> window = new HashSet<>();

    //     int left = 0, right = 0, maxLength = 0;
    //     for(; right < s.length(); right++) {
    //         Character c = s.charAt(right);

    //         while(window.contains(c)) {
