<style>
  body {
    font-family: "Comic Sans MS", "Segoe UI", sans-serif;
    background: white;
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100vh;
    overflow: hidden;
  }
  #clippy-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    position: relative;
  }
  #clippy {
    width: 140px;
    height: auto;
    user-select: none;
    animation: floaty 3s ease-in-out infinite;
  }
  @keyframes floaty {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-5px); }
  }

  /* Old Clippy Bubble */
  .speech {
    background: #ffffe0;
    border: 2px solid #e5d36b;
    border-radius: 12px;
    padding: 12px 16px;
    max-width: 300px;
    color: #333;
    font-size: 14px;
    box-shadow: 2px 2px 6px rgba(0,0,0,0.2);
    position: absolute;
    bottom: 170px;
    opacity: 0;
    transform: scale(0.9);
    animation: popUp 0.25s ease-out forwards;
  }
  @keyframes popUp {
    from { opacity: 0; transform: scale(0.8) translateY(10px); }
    to { opacity: 1; transform: scale(1) translateY(0); }
  }
  .speech::after {
    content: "";
    position: absolute;
    bottom: -16px;
    left: 30px;
    border-width: 8px;
    border-style: solid;
    border-color: #ffffe0 transparent transparent transparent;
    filter: drop-shadow(1px 1px 1px rgba(0,0,0,0.1));
  }

  #input-area {
    display: flex;
    gap: 8px;
    margin-top: 30px;
  }
  #user-input {
    padding: 10px 14px;
    border-radius: 9999px;
    border: 1px solid #ccc;
    outline: none;
    width: 220px;
  }
  #user-input:focus {
    border-color: #888;
  }
  #send-btn {
    background: #0078d7;
    color: white;
    border: none;
    border-radius: 9999px;
    padding: 10px 18px;
    cursor: pointer;
    font-weight: bold;
    transition: background 0.2s;
  }
  #send-btn:hover {
    background: #005ea6;
  }
</style>
</head>
<body>
  <div id="clippy-container">
    <img id="clippy" src="https://static.wikia.nocookie.net/18056f4d-426a-4238-a766-85f5fcf0d59b/scale-to-width/755" alt="Clippy">
    <div id="bubble-area"></div>
    <div id="input-area">
      <input id="user-input" type="text" placeholder="Ask Clippy something..." />
      <button id="send-btn">Send</button>
    </div>
  </div>

  <script>
    const apiKey = "AIzaSyCzNQXv5QifWxlaWu0urbs2FZZrSLrj-Cg";
    const model = "gemini-2.5-flash-preview-05-20";
    const bubbleArea = document.getElementById("bubble-area");
    const userInput = document.getElementById("user-input");
    const sendBtn = document.getElementById("send-btn");

    let chatHistory = [];

    function showBubble(text) {
      const old = bubbleArea.querySelector(".speech");
      if (old) old.remove();
      const bubble = document.createElement("div");
      bubble.className = "speech";
      bubble.textContent = text;
      bubbleArea.appendChild(bubble);
    }

    async function sendMessage() {
      const text = userInput.value.trim();
      if (!text) return;
      showBubble("Hmm... let me think! 🧐");
      userInput.value = "";

      chatHistory.push({ role: "user", parts: [{ text }] });

      try {
        const res = await fetch(\`https://generativelanguage.googleapis.com/v1beta/models/\${model}:generateContent?key=\${apiKey}\`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ contents: chatHistory })
        });
        const data = await res.json();
        const aiText = data?.candidates?.[0]?.content?.parts?.[0]?.text || "Sorry, my paperclip brain glitched 😅";

        chatHistory.push({ role: "model", parts: [{ text: aiText }] });
        showBubble(aiText);
      } catch (err) {
        console.error(err);
        showBubble("Oops! I lost connection to my office server! 📎💥");
      }
    }

    sendBtn.addEventListener("click", sendMessage);
    userInput.addEventListener("keydown", e => {
      if (e.key === "Enter") sendMessage();
    });

    showBubble("Hey there! It looks like you’re trying to chat with Clippy 🤓");
  </script>
</body>
</html>
`;

const clippyWin = window.open("about:blank", "_blank");
if (clippyWin) {
  clippyWin.document.write(htmlContent);
  clippyWin.document.close();
