        return 0;
    }
    
    scanf("%d", &m);
    head = init( m);
    
	for(int i = 1; i < n; i++) 
	{
        scanf("%d" , &m);
        addend(&head, m);
    }
    
    scanf("%d%d", &m, &nege);
    addn(&head, m, nege);
    
           show(head);
    ja_svoboden(head);

return 0;
