    public int longestConsecutive(int[] nums) {
        if(nums.length == 0) {
            return 0;
        }

        Set<Integer> set = new HashSet<>();

        for(int num: nums) {
            set.add(num);
        }

        int maxCount = 1;
        for(int num: set) {
            if(!set.contains(num - 1)) {
                int count = 1;
                while(set.contains(num + count)) {
                    count++;
                }

                maxCount = Math.max(count, maxCount);
            }
            
        }

        return maxCount;
    }
}

