bool dali_e_palindrom(int x) {
    vector<int> v;
    
    while(x > 0) {
        int cifra = x % 10;
        v.push_back(cifra);
        
        x /= 10;
    }
    
    int i = 0;
    int j = (int) v.size() - 1;
    
    while(i < j) {
        if(v[i] != v[j]) {
            return false;
        }
        i++;
        j--;
    }
    return true;
    
    
