class Matrica:
    def __init__(self, m, n, vrijednost=0):
        if m <= 0 or n <= 0:
            raise ValueError("Dimenzije moraju biti pozitivne cijele vrijednosti!")
        self.m = m
        self.n = n
        self.podaci = [[vrijednost for _ in range(n)] for _ in range(m)]

    def get(self, i, j):
        if not (0 <= i < self.m and 0 <= j < self.n):
            raise IndexError("Indeksi izvan granica matrice!")
        return self.podaci[i][j]

    def set(self, i, j, v):
        if not (0 <= i < self.m and 0 <= j < self.n):
            raise IndexError("Indeksi izvan granica matrice!")
        self.podaci[i][j] = v

    def transponiraj(self):
        nova = Matrica(self.n, self.m)
        for i in range(self.m):
            for j in range(self.n):
                nova.podaci[j][i] = self.podaci[i][j]
        return nova

    def __repr__(self):
        s = ""
        for red in self.podaci:
            s += " ".join(f"{x:6}" for x in red) + "\n"
        return s

